import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: ""
  });

  const [usersList,setUsersList] = useState()

  const { name, email, phone } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCreateUser = async (e) => {
    e.preventDefault(); // Prevent form submission

    try {
      // Make an API call to create a user
      const response = await axios.post("http://localhost:5000/api/createList", {
        name,
        email,
        phone
      });

      console.log(response.data, "response");
    } catch (error) {
      console.error(error);
    }
  };


  const fetchUsers = async () => {
    try {
      // Make an API call to get a list of users
      const response = await axios.get("http://localhost:5000/api/getUsers");

      setUsersList(response)
      console.log(response?.data[0], "userList");
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(()=>{
    fetchUsers()
  },[])


  return (
    <div className="App">
      <form onSubmit={handleCreateUser}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={name}
            onChange={handleChange}
          />
        </label>
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={email}
            onChange={handleChange}
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={phone}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Create User</button>
      </form>


      <div>
       {usersList?.data.map((item,index)=>(
        <><p> {item?.name} </p><p> {item?.name} </p></> 
       ))}
      </div>
    </div>
  );
}

export default App;
